package org.springdoc.webmvc.api;

import com.fasterxml.jackson.core.JsonProcessingException;
import io.swagger.v3.oas.annotations.Operation;
import java.util.List;
import java.util.Locale;
import javax.servlet.http.HttpServletRequest;
import org.springdoc.core.AbstractRequestService;
import org.springdoc.core.GenericResponseService;
import org.springdoc.core.GroupedOpenApi;
import org.springdoc.core.OpenAPIService;
import org.springdoc.core.OperationService;
import org.springdoc.core.SpringDocConfigProperties;
import org.springdoc.core.SpringDocProviders;
import org.springdoc.core.customizers.SpringDocCustomizers;
import org.springframework.beans.factory.ObjectFactory;
import org.springframework.boot.actuate.endpoint.web.annotation.RestControllerEndpoint;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PathVariable;

@RestControllerEndpoint(
   id = "openapi"
)
public class MultipleOpenApiActuatorResource extends MultipleOpenApiResource {
   public MultipleOpenApiActuatorResource(List<GroupedOpenApi> groupedOpenApis, ObjectFactory<OpenAPIService> defaultOpenAPIBuilder, AbstractRequestService requestBuilder, GenericResponseService responseBuilder, OperationService operationParser, SpringDocConfigProperties springDocConfigProperties, SpringDocProviders springDocProviders, SpringDocCustomizers springDocCustomizers) {
      super(groupedOpenApis, defaultOpenAPIBuilder, requestBuilder, responseBuilder, operationParser, springDocConfigProperties, springDocProviders, springDocCustomizers);
   }

   @Operation(
      hidden = true
   )
   @GetMapping(
      value = {"/{group}"},
      produces = {"application/json"}
   )
   public byte[] openapiJson(HttpServletRequest request, @PathVariable String group, Locale locale) throws JsonProcessingException {
      return this.getOpenApiResourceOrThrow(group).openapiJson(request, "/" + group, locale);
   }

   @Operation(
      hidden = true
   )
   @GetMapping(
      value = {"/{group}/yaml"},
      produces = {"application/vnd.oai.openapi"}
   )
   public byte[] openapiYaml(HttpServletRequest request, @PathVariable String group, Locale locale) throws JsonProcessingException {
      return this.getOpenApiResourceOrThrow(group).openapiYaml(request, "/" + group, locale);
   }
}
