package org.springdoc.webmvc.core;

import java.util.List;
import java.util.Optional;
import javax.servlet.ServletRequest;
import javax.servlet.ServletResponse;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;
import org.springdoc.core.AbstractRequestService;
import org.springdoc.core.GenericParameterService;
import org.springdoc.core.OperationService;
import org.springdoc.core.RequestBodyService;
import org.springdoc.core.SpringDocUtils;
import org.springdoc.core.customizers.ParameterCustomizer;
import org.springframework.core.LocalVariableTableParameterNameDiscoverer;

public class RequestService extends AbstractRequestService {
   public RequestService(GenericParameterService parameterBuilder, RequestBodyService requestBodyService, OperationService operationService, Optional<List<ParameterCustomizer>> parameterCustomizers, LocalVariableTableParameterNameDiscoverer localSpringDocParameterNameDiscoverer) {
      super(parameterBuilder, requestBodyService, operationService, parameterCustomizers, localSpringDocParameterNameDiscoverer);
   }

   static {
      SpringDocUtils.getConfig().addRequestWrapperToIgnore(new Class[]{ServletRequest.class}).addRequestWrapperToIgnore(new Class[]{ServletResponse.class}).addRequestWrapperToIgnore(new Class[]{HttpServletRequest.class}).addRequestWrapperToIgnore(new Class[]{HttpServletResponse.class}).addRequestWrapperToIgnore(new Class[]{HttpSession.class});
   }
}
