package org.springdoc.webmvc.ui;

import io.swagger.v3.oas.annotations.Operation;
import java.util.Map;
import javax.servlet.http.HttpServletRequest;
import org.springdoc.core.SpringDocConfigProperties;
import org.springdoc.core.SwaggerUiConfigParameters;
import org.springdoc.core.SwaggerUiConfigProperties;
import org.springframework.boot.actuate.autoconfigure.endpoint.web.WebEndpointProperties;
import org.springframework.boot.actuate.endpoint.web.annotation.ControllerEndpoint;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.ResponseBody;

@ControllerEndpoint(
   id = "swagger-ui"
)
public class SwaggerWelcomeActuator extends SwaggerWelcomeCommon {
   private static final String SWAGGER_CONFIG_ACTUATOR_URL = "/swagger-config";
   private final WebEndpointProperties webEndpointProperties;

   public SwaggerWelcomeActuator(SwaggerUiConfigProperties swaggerUiConfig, SpringDocConfigProperties springDocConfigProperties, SwaggerUiConfigParameters swaggerUiConfigParameters, WebEndpointProperties webEndpointProperties) {
      super(swaggerUiConfig, springDocConfigProperties, swaggerUiConfigParameters);
      this.webEndpointProperties = webEndpointProperties;
   }

   @Operation(
      hidden = true
   )
   @GetMapping({"/"})
   public ResponseEntity<Void> redirectToUi(HttpServletRequest request) {
      return super.redirectToUi(request);
   }

   @Operation(
      hidden = true
   )
   @GetMapping(
      value = {"/swagger-config"},
      produces = {"application/json"}
   )
   @ResponseBody
   public Map<String, Object> openapiJson(HttpServletRequest request) {
      return super.openapiJson(request);
   }

   protected void calculateUiRootPath(StringBuilder... sbUrls) {
      StringBuilder sbUrl = new StringBuilder();
      sbUrl.append(this.webEndpointProperties.getBasePath());
      this.calculateUiRootCommon(sbUrl, sbUrls);
   }

   protected String buildApiDocUrl() {
      return this.buildUrl(this.contextPath + this.webEndpointProperties.getBasePath(), "openapi");
   }

   protected String buildUrlWithContextPath(String swaggerUiUrl) {
      return this.buildUrl(this.contextPath + this.webEndpointProperties.getBasePath(), swaggerUiUrl);
   }

   protected String buildSwaggerConfigUrl() {
      return this.contextPath + this.webEndpointProperties.getBasePath() + "/" + "swagger-ui" + "/" + "swagger-config";
   }
}
