      private String path = "/v3/api-docs";
      private boolean enabled = true;
      private boolean resolveSchemaProperties;
      private boolean resolveExtensionsProperties;
      private SpringDocConfigProperties.Groups groups = new SpringDocConfigProperties.Groups();
      private SpringDocConfigProperties.ApiDocs.OpenApiVersion version;

